        public static bool Password(string password, ref string message)
        {
            if (password == null)
            {
                return false;
            }
            bool isValid = true;
            Regex []pattern = { new Regex("[a-z]"), new Regex("[A-Z]"), new Regex("[0-9]") };

            if (password.Length < 8 || password.Length > 24)
            {
                message = "The password should be between 8 - 24 characters! ";
               
                isValid = false;
            }

            if ( (pattern[0].Match(password).Success && 
                pattern[1].Match(password).Success && 
                pattern[2].Match(password).Success) == false
              )
            {
                message += "The password must contain a number, a lower and upper case character!";
                isValid = false;
            }
            return isValid;
        }
